package com.demo;

public class StringDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "Somnath";

		System.out.println("Name : " + name);
		name.concat(" Hazra");
		System.out.println("Name : " + name);

		String name2 = new String("Ayan");

		System.out.println("Name : " + name2);
		name2.concat(" Das");
		System.out.println("Name : " + name2);

		// String class immutable

	}

}
